import { async } from "@firebase/util"
import * as firebase from "firebase"
import "firebase/firestore"
import { Alert } from "react-native"

export async function registration(Class, Id, password, navigation){
    try{
        console.log.apply(Class, Id, password)
        await firebase.auth().createUserWithEmailAndPassword(Id, password);
        const currentUser = firebase.auth().currentUser;
        const db = firebase.firestore();
        db.collection("user")
        .doc(currentUser.uid)
        .set({
            Id : currentUser.Id,
            Class : Class
        })

    }catch(error){
        Alert.alert("문제 발생" , error.message)
    }
}

export default Sign;
