import { View, Text, Button, StyleSheet, Image, TextInput, TouchableOpacity } from 'react-native';
import { useEffect, useState} from 'react'
import { db } from "../firebaseConfig";
import { async } from "@firebase/util";
import Logo from '../assets/logo.png'
import User from '../assets/user.png'
import Password from '../assets/password.png'
import { addDoc, collection, getDocs, doc, updateDoc, deleteDoc, where, query, Firestore } from "firebase/firestore";


export default function LoginScreen(props) {
    const [userName,setUserName] = useState("")
    const [userPassword,setUserPassword] = useState("");
    const [addId, setAddId] = useState("");
    const [addPassword, setAddPassword] = useState("");
    const [addClass, setAddClass] = useState();
    const [users, setUsers] = useState();
    const [id, setID] = useState('');

    const onChangeInput = (event) =>{
        setUserName(event)
    }
    return (
        <View style={styles.container}>
            <View>
                <Image 
                    source={User}
                    style={styles.userIcon}
                />
                <Image 
                    source={Password}
                    style={styles.passwordIcon}
                />
            </View>
            <Image
                source={Logo}
                style={styles.appLogo}
            />
            <Text style={styles.loginText}>please sign in to continue</Text>

            <TextInput 
                style={styles.input}
                placeholder={'User name'}
                maxLength={15}
                value={userName}
                onChangeText={setUserName}
            />
            <TextInput 
                style={styles.input}
                placeholder={'Password'}
                value={userPassword}
                maxLength={15}
                onChangeText={setUserPassword}
            />
            <Button 
                title='LOGIN'
                color="#6C4BC7"
                onPress={()=>{
                    {readfromDB}
                    if(userName == addId){
                    }else{
                        alert('false')
                    }
                }}
            />

            <TouchableOpacity onPress={()=> {props.navigation.navigate("SignUp")}}>
                <Text style={styles.signUp}>Sign Up</Text>
            </TouchableOpacity>
            <Button title="Read Text" onPress={readfromDB}/>
            {users?.map((row, idx) =>{
            return(
                <>
                    <Text>User- {idx}</Text>
                    <Text>{row.addId}</Text>
                    <Text>{row.addPassword}</Text>
                </>
            )
         })}


        </View>

    );
  }
  const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        width:'100%'
      },
    appLogo: {
        width:140,
        height:140, 
        marginBottom:40,
      },
    loginText: {
